        {open ? (
          <div className={classes.dropdown}>
            Click me, I will stay visible until you click outside.
          </div>
        ) : null}
      </div>
    </ClickAwayListener>
        </div>
        <div ref={messagesEndRef} />

</ClickAwayListener>
        </>
    )
}

export default ChatProfile
