import React,{useState,useEffect} from 'react'
import Avatar from '@material-ui/core/Avatar';
import './Home.css'
import ImageOutlinedIcon from '@material-ui/icons/ImageOutlined';
import PollOutlinedIcon from '@material-ui/icons/PollOutlined';
import Axios from 'axios'
import Post from './Post'
import Poll from './Poll'
import { useSelector } from "react-redux";
import Modal from '@material-ui/core/Modal';
function Home() {
    //post
    const [post, setPost]=useState("");
    const [posts, setPosts]= useState([])
    const[postsArr, setPostsArr] = useState([])
    const [file, setFile] = useState('');
    //modal
    const [open, setOpen] = useState(false);
    //poll
    const [title, setTitle] = useState('')
    const [option1, setOption1] = useState('')
    const [option2, setOption2] = useState('')
    const [option3, setOption3] = useState('')
    const[poll,setPoll] = useState([])
    const[polls,setPolls] = useState([])
 //modal
    const handleOpen = () => {
      setOpen(true);
    };
    const handleClose = () => {
      setOpen(false);
    };

    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;
   //get data

   useEffect(() => {

       let flag = true
       Axios.get("https://mern-twitter-clone-project.herokuapp.com/pollsData", )
           .then((res) => {
               if (flag) {
                   setPolls(res.data);
               }
           })
       Axios.get("https://mern-twitter-clone-project.herokuapp.com/read", )
           .then((res) => {
               if (flag) {
                   setPostsArr(res.data);
               }
           })
       return () => flag = false
   }, [polls])

        const onChangeFile = (e) =>{
            setFile(e.target.files[0]);
        }

    const AddPosts2 =  ()  =>{
        const formData = new FormData();
        formData.append("post", post)

        Axios.post("https://mern-twitter-clone-project.herokuapp.com/insert2",formData)
        .then((res)=>setPosts(res.data))
        .catch((err)=>{
            console.log(err);
        })

    }

    const AddPosts =  ()  =>{
        const formData = new FormData();
        formData.append("post", post)
        formData.append("postImage", file)

        Axios.post("https://mern-twitter-clone-project.herokuapp.com/insert",formData)
        .then((res)=>setPosts(res.data))
        .catch((err)=>{
            console.log(err);
        })

    }

    const AddPoll =  (e)  =>{
        e.preventDefault()
        setOpen(false)
        Axios.post("https://mern-twitter-clone-project.herokuapp.com/poll",{
           title:title,
           option1:option1,
           option2:option2,
           option3:option3,
        })
        .then((res)=>setPoll(res.data))
        .catch((err)=>{
            console.log(err);
        })

    }

    const body = (
        <div className="modal_body">
            <h1>Make a poll</h1>
            <div className="modal_question">
                    <textarea
                    onChange={(e)=>setTitle(e.target.value)}
                    wrap="soft" 
                    placeholder='Ask a question...' 
                    style={{resize:'none'}}></textarea>
            </div>
            <div className="modal_body_cont">
                <input onChange={(e)=>{setOption1(e.target.value)}} type="text" placeholder='Choice 1' />
                <input onChange={(e)=>{setOption2(e.target.value)}} type="text" placeholder='Choice 2' />
                <input onChange={(e)=>{setOption3(e.target.value)}} type="text" placeholder='Choice 3' />
            </div>
            <div className="modal_button">
                <button type='submit' onClick={AddPoll}>Tweet</button>
            </div>

        </div>
      );
    return (
        <div className='home'>
        <div className='home_cont'>

            <div className="home_title">
                <h3>Home</h3>
            </div>

            <div className="home__profile_cont">
            <div className="home__profile">
                <Avatar src={userInfo?.pic} style={{width:'50px',height:'50px'}} />
                <div className="home__profile_input">
                    <input
                    onChange={(e)=>setPost(e.target.value)}
                    type="text"  
                    placeholder="What's happening?"/>
                </div>

                </div>
                <form >

                <div className="home__actions">
                    <div className="home__actions_icons">
                    <div className="home__uploadImage">
                        <label htmlFor="postImage">
                            <ImageOutlinedIcon style={{color:'rgba(29,161,242,1.00)',cursor:'pointer'}}/>
                        </label>
                        <input id="postImage" 
                        onChange={onChangeFile} 
                        name="postImage" 
                        type="file" 
                        />

                    </div>

                        <PollOutlinedIcon onClick={handleOpen} style={{color:'rgba(29,161,242,1.00)',cursor:'pointer'}}/>
                    </div>
                    <button onClick={ file ? AddPosts : AddPosts2} type='submit'>Tweet</button>
                </div>
                </form>
            </div>
            </div>
            <div className="reverse">

            <div className="Posts">
                  {postsArr.map((value)=>{
                  return (
                      <Post
                      key={value._id}
                      id={value._id}
                      post={value.posting}
                      image={value.img}
                      />
                  )
              })}
            </div>

            <div className="Polls">
                  {polls.map((val)=>{
                  return (
                      <Poll
                      key={val._id}
                      id={val._id}
                      title={val.title}
                      option1={val.option1}
                      option2={val.option2}
                      option3={val.option3}
                      />
                  )
              })}
            </div>
            <div className='modal'>
            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description"
             >
                {body}
            </Modal>
            </div>
            </div>
        </div>
    )
}

export default Home
